<?php require "config.php"; $conn = new mysqli(DB_HOST, DB_USER, DB_PASS, DB_NAME); $result = $conn->query("SELECT a.id, CONCAT(p.first_name, \" \", p.last_name) as patient, a.status FROM appointments a JOIN patients p ON a.patient_id = p.id LIMIT 5"); echo "Available appointments:\n"; while($row = $result->fetch_assoc()) { echo "ID: " . $row["id"] . " | Patient: " . $row["patient"] . " | Status: " . $row["status"] . "\n"; }
